)

mapper.update:
UPDATE SET GREENTALK
      GREEN_CONTENT = #{green_content}
    , GREEN_TAG = #{green_tag}
    , GREEN_MOD = #{green_mod}
    WHERE GREEN_ID = #{green_id}

