mapper.insert:
INSERT INTO MEMBER(
      LOGIN_ID
    , PASSWORD
    , MEM_NAME
    , MEM_NICKNAME
    , MEM_EMAIL
    , MEM_ZIPCODE
    , MEM_ADDR
    , MEM_ADDR_DETAIL
    , MEM_PIC
) VALUES (
      #{loginId}
    , #{password}
    , #{memName}
    , #{memNickname}
    , #{memEmail}
    , #{zipcode}
    , #{address}
    , #{addressDetail}
    , #{picture}
)

mapper.update:
UPDATE MEMBER
    SET
        MEM_NICKNAME = #{memNickname},
        PASSWORD = #{password},
        MEM_PIC = #{picture}
    WHERE MEM_ID = #{memId}
